class SPA{constructor(route){this.content=document.querySelector("div.content");this.loading=document.querySelector("div.loading").classList;this.Model=new Model;this.view=new View;this.controller=new Controller(this.Model);window.addEventListener("hashchange",()=>{this.loadingStart();this.Model.dataBind={};let page=`${window.location.hash.slice(1).split("?")[0]}`;document.body.id=page;this.controller[page]().then(()=>{this.renderContent(this.view[page]).bindModelText().parseEvents().twoWayFormBind().loadingEnd().cleanNavLinks()}).catch(err=>{console.error(err);this.renderContent(this.Model.escapeHTML(err)).loadingEnd().cleanNavLinks()});window.location.href.replace(window.location.search,"")});if(!window.location.hash&&typeof route==="string"){window.location.hash=route}window.dispatchEvent(new HashChangeEvent("hashchange"))}loadingStart(){this.loading.add("visible");return this}loadingEnd(){this.loading.remove("visible");return this}renderContent(html){this.content.innerHTML=html;return this}update(evt,funcName){this.Model[funcName](evt).then(()=>{this.bindModelText().parseEvents().twoWayFormBind()})}cleanNavLinks(){let links=document.querySelector("nav").querySelectorAll("a");links.forEach(link=>{link.setAttribute("href",`${window.location.origin}${link.hash}`)});return this}parseEvents(){let contents=this.content.querySelectorAll("*[data-event]");contents.forEach(domElem=>{const[evtName,funcName]=domElem.dataset.event.split(":");domElem.addEventListener(evtName,evt=>{return this.update(evt,funcName)});delete domElem.dataset.event});return this}twoWayFormBind(){let form=this.content.querySelector("form[data-bindall]");if(form){form.addEventListener("change",event=>{const target=event.target;const property=target.name;if(property&&target.matches("input, select, textarea")){this.Model.dataBind[property]=target.value}});delete form.dataset.bindall}return this}bindModelText(){let contents=this.content.querySelectorAll("*[data-bindtext], input[name], select[name], textarea[name]");const obj=this.Model.dataBind;if(contents){contents.forEach(domElem=>{const property=domElem.name||domElem.dataset.bindtext;const selector=`*[data-bindText="${property}"], input[name="${property}"], select[name="${property}"], textarea[name="${property}"]`;let val,safeVal;const useSafeHTML=domElem.hasAttribute("data-safe");if(obj.hasOwnProperty(property)&&obj[property]!==undefined){val=obj[property];safeVal=this.Model.escapeHTML(val);if("value"in domElem)domElem.value=useSafeHTML?safeVal:val;else if("innerHTML"in domElem)domElem.innerHTML=useSafeHTML?safeVal:val}Object.defineProperty(obj,property,{get:()=>{return val},set:newValue=>{let elems=document.querySelectorAll(selector);val=newValue;safeVal=this.Model.escapeHTML(val);if(elems){elems.forEach(elem=>{if("value"in elem)elem.value=useSafeHTML?safeVal:val;else if("innerHTML"in elem)elem.innerHTML=useSafeHTML?safeVal:val})}},configurable:true})})}return this}}class BaseModel{constructor(){this.APIS={};this.dataBind={};this.http={get:url=>{return this.httpFetch(url,null,"GET").then(response=>response.json())},post:(url,data)=>{return this.httpFetch(url,data,"POST").then(response=>response.json())},put:(url,data)=>{return this.httpFetch(url,data,"PUT").then(response=>response.json())},delete:url=>{return this.httpFetch(url,null,"DELETE")}}}escapeHTML(html){let div=document.createElement("div");div.appendChild(document.createTextNode(html));return div.innerHTML}httpFetch(url,data,verb){let myHeaders=new Headers;myHeaders.set("Content-Type","application/json");let myInit={method:verb,headers:myHeaders,mode:"cors",cache:"default"};if(data){myInit.body=JSON.stringify(data)}const myRequest=new Request(url,myInit);return fetch(myRequest).then(response=>{if(!response.ok)throw Error(response.statusText);return response})}generateUrlParams(params={}){return`?${Object.keys(params).map(k=>[k,params[k]].map(window.encodeURIComponent).join("=")).join("&")}`}urlParams(){return new URLSearchParams(window.location.search)}}class Components{static todoTable(data){if(!Array.isArray(data))return"";return`${data.map(row=>`<tr>\n                        <td>${row.id}</td>\n                        <td>${row.title}</td>\n                        <td>${row.completed}</td>\n                        <td><button data-id="${row.id}" data-event="click:deleteTodo" class="button is-danger">Delete</button></td>\n                        <td><button data-id="${row.id}" data-event="click:goToUpdatePage" class="button is-link">Update</button></td>\n                    </tr>`).join("")}`}}class Controller{constructor(model){this.Model=model}home(){return this.Model.getTodoList()}add(){return Promise.resolve()}update(){return this.Model.updatePageLoad()}}class Model extends BaseModel{constructor(){super();this.APIS={Todo:`//${window.location.hostname}:3001/todo/`}}getTodoList(){return this.http.get(this.APIS.Todo).then(data=>{return this.dataBind.todoTable=Components.todoTable(data)})}deleteTodo(evt){const url=`${this.APIS.Todo}${evt.target.dataset.id}`;return this.http.delete(url).then(()=>{return this.dataBind.deleteResultMsg="Todo Deleted"}).catch(err=>{return this.dataBind.deleteResultMsg="Todo was NOT Deleted"}).then(()=>{return this.getTodoList()})}saveTodo(){const data={title:this.dataBind.title,completed:this.dataBind.completed};return this.http.post(this.APIS.Todo,data).then(data=>{this.dataBind.saveResultMsg="Todo Saved";return data}).catch(err=>{this.dataBind.saveResultMsg="Todo was NOT Saved";return err})}goToUpdatePage(evt){this.redirect("update",{id:evt.target.dataset.id});return Promise.resolve()}updatePageLoad(){const url=`${this.APIS.Todo}${this.urlParams().get("id")}`;return this.http.get(url).then(data=>{this.dataBind.title=data.title;this.dataBind.completed=data.completed;this.dataBind.id=data.id;return data})}updateTodo(){const data={title:this.dataBind.title,completed:this.dataBind.completed};const url=`${this.APIS.Todo}${this.dataBind.id}`;return this.http.put(url,data).then(data=>{this.dataBind.updateResultMsg="Todo updated";return data}).catch(err=>{this.dataBind.updateResultMsg="Todo was NOT updated";return err})}}class View{get home(){return`<section class="hero is-info is-small">\n                    <div class="hero-body">\n                        <h1 class="title">Todo Crud Sample</h1>\n                    </div>\n                </section>\n                <p data-bindtext="deleteResultMsg"></p>\n                <table class="table is-bordered">\n                  <thead>\n                    <tr>\n                        <th>ID</th>\n                        <th>Title</th>\n                        <th>Completed</th>\n                        <th></th>\n                        <th></th>\n                    </tr>\n                  </thead>\n                  <tbody data-bindtext="todoTable"></tbody>\n              </table>`}get add(){return`<section class="hero is-info is-small">\n                    <div class="hero-body">\n                        <h1 class="title">Add New Todo</h1>\n                    </div>\n                </section>\n                <form data-bindall>\n                    <p>\n                        <label>Title</label>\n                        <input type="text" name="title" />\n                    </p>\n                    <p>\n                        <label>Completed</label>\n                        <select name="completed">\n                            <option value="true">true</option>\n                            <option value="false">false</option>\n                        </select>\n                    </p>\n                    <p data-bindtext="saveResultMsg"></p>\n                    <p> <input type="button" value="submit" data-event="click:saveTodo" /> </p>\n                </form>`}get update(){return`<section class="hero is-info is-small">\n                    <div class="hero-body">\n                        <h1 class="title">Update Todo ID <span data-bindtext="id" class="has-text-warning"></span></h1>\n                    </div>\n                </section>\n                <form data-bindall>                    \n                    <p>\n                        <label>Title</label>\n                        <input type="text" name="title" />\n                    </p>\n                    <p>\n                        <label>Completed</label>\n                        <select name="completed">\n                            <option value="true">true</option>\n                            <option value="false">false</option>\n                        </select>\n                    </p>\n                    <p data-bindtext="updateResultMsg" data-safe></p>\n                    <p> <input type="button" value="submit" data-event="click:updateTodo" /> </p>\n                </form>`}}Object.assign(BaseModel.prototype,{redirect(route=window.location.hash.slice(1).split("?")[0],params={}){const query=this.generateUrlParams(params);window.location.assign(`${query}#${route}`)}});